int main()
{
    int i;
    // 声明i为整数类型

    // 使用for循环遍历0到5的整数
    for(i=0; i<6; i++)
    {
        // 输出当前整数i以及它的阶乘值
        printf("%d!=%d\n",i,fact(i));
    }
}

// 定义fact函数，接受一个整数参数j，并返回该整数的阶乘值
// 如果j等于0，则返回1
// 否则，将j乘以fact(j-1)的结果，并返回
// 这个函数是一个递归函数，用于计算阶乘值
int fact(int j)
{
    int sum;
    // 声明sum为整数类型

    // 如果j等于0，则将sum设置为1
    if(j==0)
    {
        sum=1;
    }
    // 否则，将j乘以fact(j-1)的结果，并将结果存储在sum中
    else
    {
        sum=j*fact(j-1);
    }
    // 返回sum的值
    return sum;
}
