#include <stdio.h>
#include <stdlib.h>
#include <string.h>

void find_sum(int nums[], int target, int *result_index1, int *result_index2) {
    int hash_table[10000] = {0};
    *result_index1 = -1;
    *result_index2 = -1;

    for (int i = 0; i < sizeof(nums) / sizeof(nums[0]); i++) {
        int complement = target - nums[i];
        if (hash_table[complement] != 0) {
            if (hash_table[complement] == i) {
                *result_index1 = i;
                *result_index2 = hash_table[complement];
            } else {
                *result_index1 = hash_table[complement];
                *result_index2 = i;
            }
            return;
        }
        hash_table[nums[i]] = i;
    }
}

int main() {
    int nums[] = {2, 7, 11, 15};
    int target = 9;
    int result_index1, result_index2;
    find_sum(nums, target, &result_index1, &result_index2);
