	"strings"
)

func distributeCoins(names []string) int {
	total := 0

	for _, name := range names {
		count := 0
		for _, char := range name {
			switch strings.ToLower(string(char)) {
			case "e":
				count++
			case "i":
				count += 2
			case "o":
				count += 3
			case "u":
				count += 4
			}
		}
		total += count
	}

	return total
}

