def binary_search(arr, target):
    left, right = 0, len(arr) - 1

    while left <= right:
        mid = (left + right) // 2
        if arr[mid] == target:
            return mid  # 找到目标值，返回索引位置
        elif arr[mid] < target:
            left = mid + 1  # 在右侧子数组继续查找
        else:
            right = mid - 1  # 在左侧子数组继续查找
    return None  # 如果未找到目标值，返回None

